import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import useFetch from "../hooks/useFetch"; // Assure-toi que le hook useFetch est bien implémenté

// Styles principaux
const LoginContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100vh;
  flex-direction: column;
`;

const GoogleLoginButtonContainer = styled.div`
  margin-top: 1rem;
`;

const Login = () => {
  const { handleGoogle, loading, error } = useFetch(
    "http://localhost:5152/login"
  );

  useEffect(() => {
    /* global google */
    if (window.google) {
      google.accounts.id.initialize({
        client_id:
          "509938645259-pmtvqe7ni98ivq69k4omgimi5mbfl4uv.apps.googleusercontent.com",
        callback: handleGoogle,
      });

      google.accounts.id.renderButton(document.getElementById("loginDiv"), {
        // type: "standard",
        theme: "filled_black",
        // size: "small",
        text: "signin_with",
        shape: "pill",
      });

      // google.accounts.id.prompt()
    }
  }, [handleGoogle]);

  return (
    <>
      <nav style={{ padding: "2rem" }}>
        <Link to="/">Go Back</Link>
      </nav>
      <LoginContainer>
        <header style={{ textAlign: "center" }}>
          <h1>Login to continue</h1>
        </header>
        <main>
          {error && <p style={{ color: "red" }}>{error}</p>}
          {loading ? (
            <div>Loading....</div>
          ) : (
            <GoogleLoginButtonContainer id="googleLoginButton"></GoogleLoginButtonContainer>
          )}
        </main>
      </LoginContainer>
      <footer></footer>
    </>
  );
};

export default Login;
