import { default as React, useEffect } from "react";
import { Link } from "react-router-dom";
import styled, { keyframes } from "styled-components";
import useFetch from "../hooks/useFetch";

const gradientAnimation = keyframes`
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
`;

// Conteneur principal
const LoginContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100vh;
  background: linear-gradient(-45deg, #ee7752, #e73c7e, #23a6d5, #23d5ab);
  background-size: 400% 400%;
  animation: ${gradientAnimation} 15s ease infinite;
`;

// Boîte de login
const LoginForm = styled.div`
  padding: 2rem;
  background: white;
  border-radius: 20px;
  box-shadow: 0 10px 25px rgba(0, 0, 0, 0.1);
  text-align: center;
  width: 400px;
`;

// Bouton Google stylisé (optionnel puisque nous utilisons le bouton Google)
const GoogleLoginButtonContainer = styled.div`
  margin-top: 1rem;
`;

const Login = () => {
  const { handleGoogle, loading, error } = useFetch(
    "http://localhost:5152/login"
  );

  useEffect(() => {
    /* global google */
    if (window.google) {
      google.accounts.id.initialize({
        client_id:
          "509938645259-pmtvqe7ni98ivq69k4omgimi5mbfl4uv.apps.googleusercontent.com",
        callback: handleGoogle,
      });

      google.accounts.id.renderButton(document.getElementById("loginDiv"), {
        // type: "standard",
        theme: "filled_black",
        // size: "small",
        text: "signin_with",
        shape: "pill",
      });

      // google.accounts.id.prompt()
    }
  }, [handleGoogle]);

  return (
    <>
      <nav style={{ padding: "2rem" }}>
        <Link to="/">Go Back</Link>
      </nav>
      <LoginContainer>
        <header style={{ textAlign: "center" }}>
          <h1>Login to continue</h1>
        </header>
        <main>
          {error && <p style={{ color: "red" }}>{error}</p>}
          {loading ? (
            <div>Loading....</div>
          ) : (
            <GoogleLoginButtonContainer id="googleLoginButton"></GoogleLoginButtonContainer>
          )}
        </main>
      </LoginContainer>
      <footer></footer>
    </>
  );
};

export default Login;
