import React, { useEffect, useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import "./HomePage.css";

const HomePage = ({ user }) => {
  const [startDate, setStartDate] = useState(new Date());
  const [endDate, setEndDate] = useState(new Date());
  const [viewByDay, setViewByDay] = useState(false);
  const [numberOfDays, setNumberOfDays] = useState(7); // Default to 7 days for 'This Week'
  const [isCustomRange, setIsCustomRange] = useState(false); // State to track if in custom range mode
  const [isCalendarVisible, setIsCalendarVisible] = useState(false); // State to track calendar visibility
  const [isCustom, setIsCustom] = useState(false); 
  const [selectedCampaign, setSelectedCampaign] = useState(0);
  const [openDetailsIndex, setOpenDetailsIndex] = useState(null);
  const [openDetailSlide, setOpenDetailSlide] = useState(false);
  const [isContactPopupVisible, setIsContactPopupVisible] = useState(false);
  const [isVideoPopupVisible, setIsVideoPopupVisible] = useState(false);



  useEffect(() => {
    const today = new Date();

    const generateRandomData = (numPoints) => Array.from({ length: numPoints }, () => Math.floor(Math.random() * 1000));

    const ctx = document.getElementById('myChart').getContext('2d');
    const gradient = ctx.createLinearGradient(0, 0, 0, 400);
    gradient.addColorStop(0, 'rgba(67, 121, 238, 0.5)');
    gradient.addColorStop(1, 'rgba(255, 255, 255, 0.01)');

    const config = {
      type: 'line',
      data: {
        labels: Array.from({ length: numberOfDays }, (_, i) => i + 1),
        datasets: [{
          label: 'Chiffre d\'affaire',
          data: generateRandomData(numberOfDays),
          backgroundColor: gradient,
          borderColor: '#00228F',
          borderWidth: 2,
          fill: true,
          pointStyle: 'circle',
          pointBackgroundColor: '#00228F',
          pointBorderColor: '#00228F',
          pointRadius: 3
        }]
      },
      options: {
        scales: {
          y: {
            beginAtZero: true,
            stacked: true,
            grid: {
              color: '#ffffff00',
            },
            ticks: {
              color: '#202224',
            }
          },
          x: {
            grid: {
              color: '#ffffff00',
            },
            ticks: {
              color: '#202224',
            }
          }
        },
        plugins: {
          legend: {
            display: false
          }
        }
      }
    };

    let myChart = new window.Chart(ctx, config);

    let selectedTimePeriod = document.getElementById('time-period');

    document.querySelector('.custom_range_btn').addEventListener('click', () => {
      setIsCalendarVisible(true);
      setIsCustomRange(true);
      setViewByDay(true);
      selectedTimePeriod.value = 'customRange';
      setIsCustom(true);
    });


    selectedTimePeriod.addEventListener('change', (e) => {
      const selectedOption = e.target.value;
      switch (selectedOption) {
        case 'thisWeek':
          setNumberOfDays(7);
          setIsCustomRange(false);
          setIsCustom(false);
          break;
        case 'lastTwoWeeks':
          setNumberOfDays(14);
          setIsCustomRange(false);
          setIsCustom(false);
          break;
        case 'thisMonth':
          setNumberOfDays(new Date(today.getFullYear(), today.getMonth() + 1, 0).getDate());
          setIsCustomRange(false);
          setIsCustom(false);
          break;
        case 'customRange':
          setIsCustomRange(true);
          setIsCustom(true);
          break;
        default:
          setNumberOfDays(7);
      }
    });

    const handlePointClick = (event) => {
      const points = myChart.getElementsAtEventForMode(event, 'nearest', { intersect: true }, false);
      if (points.length) {
        points.forEach(point => {
          const { datasetIndex, index } = point;
          handleDetailSlide();
          console.log('Point clicked - Dataset:', datasetIndex, 'Index:', index);
        });
      }
    };


    const canvas = document.getElementById('myChart');
    canvas.addEventListener('click', handlePointClick);

    return () => {
      myChart.destroy();
      canvas.removeEventListener('click', handlePointClick);
    };

  }, [startDate, endDate, viewByDay, numberOfDays, isCustomRange]);

  //title dynamic dat
  const calculateDates = (period) => {
    const today = new Date();
    let start = new Date();
    let end = new Date();

    switch (period) {
      case 'thisWeek':
        start = new Date(today.getFullYear(), today.getMonth(), today.getDate() - today.getDay());
        end = new Date(today.getFullYear(), today.getMonth(), today.getDate() - today.getDay() + 6);
        break;
      case 'lastTwoWeeks':
        start = new Date(today.getFullYear(), today.getMonth(), today.getDate() - today.getDay() - 13);
        end = new Date(today.getFullYear(), today.getMonth(), today.getDate() - today.getDay() + 6);
        break;
      case 'thisMonth':
        start = new Date(today.getFullYear(), today.getMonth(), 1);
        end = new Date(today.getFullYear(), today.getMonth() + 1, 0);
        break;
      default:
        break;
    }

    return { start, end };
  };
  const handlePeriodChange = (period) => {
    const { start, end } = calculateDates(period);
    setStartDate(start);
    setEndDate(end);
    setViewByDay(true);
    setIsCalendarVisible(false);
    setIsCustomRange(false);
    setIsCustom(false);
  };
  const handleDateChange = (dates) => {
    const [start, end] = dates;
    setStartDate(start);
    setEndDate(end);
    setViewByDay(true);
    setIsCalendarVisible(false);
    setIsCustomRange(true);
    setIsCustom(true);
  };

  // Fonction pour rendre dynamiques les dates dans le titre
  const renderDynamicDates = () => {
    if (isCustomRange) {
      return (
        <>
          <span className='graph_choosed_date_start'>{startDate.toLocaleDateString()}</span>
          <span className='graph_choosed_date_end'>{endDate.toLocaleDateString()}</span>
        </>
      );
    } else {
      const periodLabel = document.getElementById('time-period').selectedOptions[0].text;
      const { start, end } = calculateDates(periodLabel.toLowerCase());
      return (
        <>
          <span className='graph_choosed_date_start'>{start.toLocaleDateString()}</span>
          <span className='graph_choosed_date_end'>{end.toLocaleDateString()}</span>
        </>
      );
    }
  };




  const handleRadioChange = (index) => {
    setSelectedCampaign(index);
  };



  // open sessions list
  const handleDetailSlide = () => {
    setOpenDetailSlide((prev) => !prev);
  };
  const handleCloseDetails = () => {
    setOpenDetailSlide(false);
  };

  // document.querySelector('.video_tracker_page').addEventListener('click', () => {
  //   if (openDetailSlide) {
  //     setOpenDetailSlide(false);
  //   }
  // });


  // open details of one session
  const toggleDetails = (index) => {
    setOpenDetailsIndex((prevIndex) => (prevIndex === index ? null : index));

    const allDetailContents = document.querySelectorAll('.graph_point_detail_content');
    allDetailContents.forEach((detailContent, i) => {
      if (i === index && openDetailsIndex !== index) {
        detailContent.style.height = detailContent.scrollHeight + 'px';
      } else {
        detailContent.style.height = '0px';
      }
    });
  };



  // contact pop-up
  const toggleContactPopup = () => {
    setIsContactPopupVisible(!isContactPopupVisible);
  };


  // youtube tutorial video pop-up
  const toggleVideoPopup = () => {
    setIsVideoPopupVisible(!isVideoPopupVisible);
  };



  return (
    <div className='video_tracker_page'>
      {/* Pop-up de la vidéo YouTube */}
      <div className={`video_pop_up ${isVideoPopupVisible ? '' : 'hide_video_pop_up'}`}>
        <div className="video_pop_up_content">
          <i className="fa fa-times close" aria-hidden="true" onClick={toggleVideoPopup}></i>
          <h2>VideoTracker Tutorial</h2>
          <iframe width="560" height="315" src="https://www.youtube.com/embed/oJuP-4Hsvkg?si=gzN_yN6GHlPSznEL" title="YouTube video player" frameBorder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share" allowFullScreen></iframe>
          <p>In your VideoTracker page ...</p>
        </div>
      </div>


      {/* Pop-up de contact */}
      <div className={`contact_pop_up ${isContactPopupVisible ? '' : 'hide_contact_pop_up'}`}>
        <div className="contact_pop_up_content">
          <i className="fa fa-times close" aria-hidden="true" onClick={toggleContactPopup}></i>
          <textarea placeholder='Put your message here' rows={5} cols={50}></textarea>
          <button type='submit' onClick={toggleContactPopup}>Submit</button>
        </div>
      </div>


      {/* Bannière sur toute la largeur */}
      <div className="banner">
        <div className="banner-content">
          <h1 className="banner-title">Product Database</h1>
          <p className="banner-subtitle">
            Search millions of products and see their revenue
          </p>
        </div>
        <div className="banner-buttons">
          <button className="banner-button">Take Tour</button>
          <button className="banner-button" onClick={toggleVideoPopup}>Watch Tutorial</button>
        </div>
      </div>

      {/* main content */}
      <section className="dashboard-container">
        <div className="campaign_list_container">
          <div className="campaign_list">
            {Array.from({ length: 8 }, (_, index) => (
              <label className="campaign_list_card" htmlFor={`campaign-${index}`}>
                <input
                  type="radio"
                  name="campaign"
                  id={`campaign-${index}`}
                  checked={selectedCampaign === index}
                  onChange={() => handleRadioChange(index)}
                />
                <div className="campaign_list_card_title_container">
                  <h4>Campaign Name</h4>
                  <i className="fa fa-eye" aria-hidden="true"></i>
                </div>
                </label>
            ))}
          </div>
        </div>

        {/* Graphique */}
        <div className="graph">
          <div className="graph-title-container">
            <h3>Title {renderDynamicDates()}</h3>
            <div className="date-select">
              <div className="custom_range">
                <button className="custom_range_btn">Custom Range</button>
                <div className="view-toggle">
                {isCustomRange && (
                  <DatePicker
                    selected={startDate}
                    onChange={handleDateChange}
                    startDate={startDate}
                    endDate={endDate}
                    selectsRange
                    inline
                  />
                )}
              </div>
              </div>
              <select name="time-period" id="time-period" onChange={(e) => setViewByDay(e.target.value === 'thisWeek')}>
                <option value="thisWeek">This Week</option>
                <option value="lastTwoWeeks">Last Two Weeks</option>
                <option value="thisMonth">This Month</option>
                {isCustom && (
                  <option value="customRange">Custom</option>
                )}
              </select>
            </div>
          </div>
          <div className="grap">
            <canvas className="income-graph" id="myChart"></canvas>
          </div>
        </div>
      </section>

      {/* Campaign point details */}
      <div className={`graph_point_details_container ${openDetailSlide && "open"}`}>
        <div className="graph_point_details_list">
          <i className="fa fa-times close" aria-hidden="true" onClick={handleCloseDetails}></i>
          <h2>Analytics of <span>01/01/2024</span></h2>
          {/* details */}
          {Array.from({ length: 7 }, (_, index) => (
            <div className="graph_point_detail" key={index}>
              <div className="graph_point_detail_header" onClick={() => toggleDetails(index)}>
                <span className="graph_point_detail_title">01/03/2024</span>
                <div className="graph_point_detail_perc_bar_container">
                  <p><span className="perc_number">50</span> %</p>
                  <div className="graph_point_detail_perc_bar">
                    <div className='perc'></div>
                  </div>
                </div>
                <i className={`fa fa-chevron-down ${openDetailsIndex === index && "rotate"}`} aria-hidden="true"></i>
              </div>
              <div className={`graph_point_detail_content graph_point_detail_content_${index} ${openDetailsIndex === index && "open_details"}`}>
                <div className='graph_point_detail_content_data'>
                  <p>Clicked at <span className='played_at'>01/03/2024 06:45</span></p>
                  <div className='click_play_line'>
                    <div className='line'></div>
                    <i className="fa fa-chevron-down" aria-hidden="true"></i>
                  </div>
                  <p>Played at <span className='played_at'>01/03/2024 06:45</span></p>
                  <div className='play_finish_line'>
                    <div className='line'></div>
                    <i className="fa fa-chevron-down" aria-hidden="true"></i>
                  </div>
                  <p>Finished at <span className='finished_at'>01/03/2024 06:55</span></p>
                </div>
                <div className='download_button_container'>
                  <div className='download_button data'>
                    <i className="fa fa-download" aria-hidden="true"></i>
                    <p>data</p>
                  </div><div className='download_button all_data'>
                    <i className="fa fa-download" aria-hidden="true"></i>
                    <p>all data</p>
                  </div>
                </div>
              </div>
            </div>
          ))}


          {/* pages */}
          <ul className="graph_point_details_page_list">
            {Array.from({ length: 5 }, (_, index) => (
              <li className="graph_point_details_page" key={index}>
                <input
                  type="radio"
                  name="graph_point"
                  id={`graph_point_${index + 1}`}
                  value={index + 1}
                  defaultChecked={index === 0}
                />
                <label htmlFor={`graph_point_${index + 1}`}>{index + 1}</label>
              </li>
            ))}
          </ul>
        </div>
      </div>




      {/* campagin infos container */}
      <div className='campaign_details_conainer'>
        {/* campagin header infos */}
        <div className='campaign_details'>
          <div className='header_campaign_container'>
            <div className='status_container'>
              <p>Status: <span className='status'>Rendered</span></p>
            </div>
            <div className='credit_container'>
              <p>Credit Used: <span className='credit'>17</span></p>
            </div>
            <div className='campaign_id_container'>
              <p>Campaign ID: <span className='campaign_id'>y8r6jct2</span></p>
            </div>
          </div>

          {/* campagin infos */}
          <div className='infos_campaign_container'>
            <div className='views_campaign campaign_width'>
              <div className='icon_container'>
                <p>Opened</p>
                <i className="fa fa-eye" aria-hidden="true"></i>
              </div>
              <p className='views campaign_info_value'>200</p>
            </div>
            <div className='start_campaign campaign_width'>
              <div className='icon_container'>
                <p>Started</p>
                <i className="fa fa-play" aria-hidden="true"></i>
              </div>
              <p className='start campaign_info_value'>180</p>
              <div className='more_info_container'>
                <p className='title'>Open to Start Rate</p>
                <p className='start_rate perc'>89%</p>
              </div>
            </div>
            <div className='end_campaign campaign_width'>
              <div className='icon_container'>
                <p>Ended</p>
                <i className="fa fa-flag" aria-hidden="true"></i>
              </div>
              <p className='end campaign_info_value'>160</p>
              <div className='more_info_container'>
                <p className='title'>Start to End Rate</p>
                <p className='end_rate perc'>71%</p>
              </div>
            </div>
          </div>
        </div>


        {/* campagin options */}
        <div className='campaign_options_container'>
          <div className='campaign_options_title'>
            <i class="fa fa-cog fa-spin fa-2x fa-fw"></i>
            <span class="sr-only">Loading...</span>
            <p className='setting_title'>Campaign Settings</p>
          </div>
          <div className='campaign_options'>
            <div className='setting_button' onClick={toggleContactPopup}>
              <i className="fa fa-envelope" aria-hidden="true"></i>
              <p>Contact us</p>
            </div>
            <div className='setting_button'>
              <i className="fa fa-download" aria-hidden="true"></i>
              <p>download all data</p>
            </div>
            <div className='setting_button'>
              <i className="fa fa-link" aria-hidden="true"></i>
              <p>Change Calendly Link</p>
            </div>
            <div className='setting_button'>
              <i className="fa fa-trash" aria-hidden="true"></i>
              <p>Delete All Flows</p>
            </div>
          </div>
        </div>

      </div>


    </div>
  );
};

export default HomePage;
