import React, { useEffect, useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import "./HomePage.css";

const HomePage = ({ user }) => {
  const [startDate, setStartDate] = useState(new Date());
  const [endDate, setEndDate] = useState(new Date());
  const [viewByDay, setViewByDay] = useState(false);

  useEffect(() => {
    const today = new Date();
    const currentDayOfWeek = today.getDay(); // TODAY

    // Fonction pour générer des données pour les 4 dernières semaines
    const generateRandomData = (numPoints) => Array.from({ length: numPoints }, () => Math.floor(Math.random() * 1000));

    const data = {
      semaine1: generateRandomData(7),
      semaine2: generateRandomData(7),
      semaine3: generateRandomData(7),
      semaine4: generateRandomData(7)
    };

    const script = document.createElement('script');
    script.src = 'https://cdn.jsdelivr.net/npm/chart.js';
    script.async = true;
    document.body.appendChild(script);

    script.addEventListener('load', () => {
      const ctx = document.getElementById('myChart').getContext('2d');
      const gradient = ctx.createLinearGradient(0, 0, 0, 400);
      gradient.addColorStop(0, 'rgba(67, 121, 238, 0.5)');
      gradient.addColorStop(1, 'rgba(255, 255, 255, 0.01)');

      const config = {
        type: 'line',
        data: {
          labels: viewByDay ? Array.from({ length: 24 }, (_, i) => i + 1) : ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'],
          datasets: [{
            label: 'Chiffre d\'affaire',
            data: viewByDay ? generateRandomData(24) : data['semaine4'],
            backgroundColor: gradient,
            borderColor: '#4379EE',
            borderWidth: 2,
            fill: true,
            pointBackgroundColor: '#4379EE',
            pointBorderColor: '#4379EE',
            pointRadius: 3
          }]
        },
        options: {
          scales: {
            y: {
              beginAtZero: true,
              stacked: true,
              grid: {
                color: '#ffffff00',
              },
              ticks: {
                color: '#202224',
              }
            },
            x: {
              grid: {
                color: '#ffffff00',
              },
              ticks: {
                color: '#202224',
              }
            }
          },
          plugins: {
            legend: {
              display: false
            }
          }
        }
      };

      let myChart = new window.Chart(ctx, config);

      document.getElementById('time-period').addEventListener('change', (e) => {
        const selectedWeek = e.target.value;
        myChart.data.datasets[0].data = viewByDay ? generateRandomData(24) : data[selectedWeek];
        myChart.update();
      });

      return () => {
        document.body.removeChild(script);
        myChart.destroy();
      };
    });

  }, [startDate, endDate]);

  const handleDateChange = (dates) => {
    const [start, end] = dates;
    setStartDate(start);
    setEndDate(end);
    setViewByDay(true);
  };

  return (
    <section className="dashboard-container">
      <div className="graph">
        <div className="graph-title-container">
          <h3>Title</h3>
          <div className="date-select">
            <div className="view-toggle">
              <DatePicker
                selected={startDate}
                onChange={handleDateChange}
                startDate={startDate}
                endDate={endDate}
                selectsRange
                inline
              />
            </div>
            <select name="time-period" id="time-period" onChange={() => setViewByDay(false)}>
              <option value="semaine1">This Week</option>
              <option value="semaine2">Last Two Week</option>
              <option value="semaine3">This Month</option>
            </select>
          </div>
        </div>
        <div className="grap">
          <canvas className="income-graph" id="myChart"></canvas>
        </div>
      </div>
    </section>
  );
};

export default HomePage;
