import React from "react";
import { Route, Routes } from "react-router-dom";
import styled from "styled-components";
import HomePage from "../components/HomePage";
import Navbar from "../components/Navbar";
import SettingsPage from "../components/SettingsPage";
import VideoTracker from "../components/VideoTracker";

const LayoutContainer = styled.div`
  display: flex;
  flex-direction: row; // Assurez-vous que c'est bien défini si nécessaire
`;

const ContentContainer = styled.div`
  flex-grow: 1;
  width: 100%;
  box-sizing: border-box; // Ceci assure que padding et border sont inclus dans la largeur
  overflow-x: hidden; // Empêche le débordement horizontal
`;

const Dashboard = ({ user }) => {
  return (
    <LayoutContainer>
      <Navbar user={user} />
      <ContentContainer>
        <Routes>
          <Route path="/dashboard" element={<HomePage user={user} />} />
          <Route path="/settings" element={<SettingsPage user={user} />} />
          <Route path="/video-tracker" element={<VideoTracker user={user} />} />
          {/* Add more nested routes as needed */}
        </Routes>
      </ContentContainer>
    </LayoutContainer>
  );
};

export default Dashboard;
