import React from "react";
import { NavLink } from "react-router-dom";
import styled from "styled-components";
import {
  ReactComponent as DashboardIcon,
  ReactComponent as Logo,
} from "../assets/logo.svg";

// Add imports for other icons you will use
// Example:
// import { ReactComponent as SettingsIcon } from "../assets/settings.svg";

const NavbarContainer = styled.div`
  background-color: #f8f9fa; // Light grey background
  min-height: 100vh;
  width: 250px;
  border-right: 1px solid #000; // A black border as per your code
`;

const NavbarHeader = styled.div`
  padding: 2rem 1rem;
  background-color: #fff; // White background for header
  color: #333; // Dark grey text
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
`;

const UpgradeButton = styled.button`
  background-color: #007bff; // Bootstrap primary blue
  border: none;
  padding: 0.5rem 1rem;
  color: white;
  margin-top: 1rem;
  cursor: pointer;

  &:hover {
    background-color: #0056b3; // Darker blue on hover
  }
`;

const NavbarItem = styled(NavLink)`
  display: flex;
  align-items: center;
  padding: 1rem;
  color: #333;
  text-decoration: none;
  margin-left: 10px; // Indent text a bit

  &:hover {
    background-color: #f5f5f5; // Lighter grey on hover
  }

  &.active {
    background-color: #e7e7e7; // Grey background for active item
    color: #007bff; // Blue text for active item
  }
`;

const NavbarIcon = styled.span`
  margin-right: 1rem;
`;

const NavbarLabel = styled.span``;

// Add other Styled components for other icons
// Example:
// const SettingsIconStyled = styled(SettingsIcon)`
//   height: 20px;
//   width: auto;
// `;

const Navbar = ({ user }) => {
  const logout = () => {
    localStorage.removeItem("user");
    window.location.reload();
  };

  return (
    <NavbarContainer>
      <NavbarHeader>
        <Logo />
        <div>{user.name}</div>
        <div>7-Day Trial Left</div>
        <UpgradeButton>Upgrade</UpgradeButton>
      </NavbarHeader>
      <NavbarItem exact to="/dashboard">
        <NavbarIcon>
          <DashboardIcon />
        </NavbarIcon>
        <NavbarLabel>Dashboard</NavbarLabel>
      </NavbarItem>
      {/* Repeat for each navbar item, replacing DashboardIcon with the appropriate icon */}
      <NavbarItem to="/settings">
        <NavbarIcon>{/* Replace with <SettingsIconStyled /> */}</NavbarIcon>
        <NavbarLabel>Settings</NavbarLabel>
      </NavbarItem>
      {/* Continue adding NavbarItems for other routes */}
    </NavbarContainer>
  );
};

export default Navbar;
