import React from "react";
import { Link } from "react-router-dom";
import styled, { keyframes } from "styled-components";

// Animation pour le background
const gradientAnimation = keyframes`
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
`;

// Conteneur principal
const LoginContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100vh;
  background: linear-gradient(-45deg, #ee7752, #e73c7e, #23a6d5, #23d5ab);
  background-size: 400% 400%;
  animation: ${gradientAnimation} 15s ease infinite;
`;

// Boîte de login
const LoginForm = styled.div`
  padding: 2rem;
  background: white;
  border-radius: 20px;
  box-shadow: 0 10px 25px rgba(0, 0, 0, 0.1);
  text-align: center;
  width: 400px;
`;

// Titre
const Title = styled.h2`
  margin-bottom: 2rem;
  color: #333;
`;

// Champ de formulaire stylisé
const StyledInput = styled.input`
  width: 80%;
  padding: 0.5rem;
  margin-bottom: 1rem;
  border: 1px solid #ddd;
  border-radius: 20px;
  outline: none;
  box-sizing: border-box;
  &:focus {
    border-color: #23a6d5;
  }
`;

// Bouton de soumission
const SubmitButton = styled.button`
  width: 84%;
  padding: 0.5rem;
  border: none;
  background-color: #23a6d5;
  color: white;
  border-radius: 20px;
  cursor: pointer;
  transition: background-color 0.3s;
  &:hover {
    background-color: #1976d2;
  }
`;

const Login = () => {
  return (
    <LoginContainer>
      <LoginForm>
        <Title>Connexion</Title>
        <form>
          <StyledInput type="email" placeholder="Adresse email" />
          <StyledInput type="password" placeholder="Mot de passe" />
          <SubmitButton type="submit">Se connecter</SubmitButton>
        </form>
        <p>
          Pas de compte ? <Link to="/register">S'inscrire</Link>
        </p>
      </LoginForm>
    </LoginContainer>
  );
};

export default Login;
