import React, { useEffect, useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import "./HomePage.css";

const HomePage = ({ user }) => {
  const [startDate, setStartDate] = useState(new Date());
  const [endDate, setEndDate] = useState(new Date());
  const [viewByDay, setViewByDay] = useState(false);
  const [numberOfDays, setNumberOfDays] = useState(7); // Default to 7 days for 'This Week'
  const [isCustomRange, setIsCustomRange] = useState(false); // State to track if in custom range mode
  const [isCalendarVisible, setIsCalendarVisible] = useState(false); // State to track calendar visibility
  const [isCustom, setIsCustom] = useState(false); 

  const [selectedCampaign, setSelectedCampaign] = useState(0);


  useEffect(() => {
    const today = new Date();

    const generateRandomData = (numPoints) => Array.from({ length: numPoints }, () => Math.floor(Math.random() * 1000));

    const ctx = document.getElementById('myChart').getContext('2d');
    const gradient = ctx.createLinearGradient(0, 0, 0, 400);
    gradient.addColorStop(0, 'rgba(67, 121, 238, 0.5)');
    gradient.addColorStop(1, 'rgba(255, 255, 255, 0.01)');

    const config = {
      type: 'line',
      data: {
        labels: Array.from({ length: numberOfDays }, (_, i) => i + 1),
        datasets: [{
          label: 'Chiffre d\'affaire',
          data: generateRandomData(numberOfDays),
          backgroundColor: gradient,
          borderColor: '#00228F',
          borderWidth: 2,
          fill: true,
          pointBackgroundColor: '#00228F',
          pointBorderColor: '#00228F',
          pointRadius: 3
        }]
      },
      options: {
        scales: {
          y: {
            beginAtZero: true,
            stacked: true,
            grid: {
              color: '#ffffff00',
            },
            ticks: {
              color: '#202224',
            }
          },
          x: {
            grid: {
              color: '#ffffff00',
            },
            ticks: {
              color: '#202224',
            }
          }
        },
        plugins: {
          legend: {
            display: false
          }
        }
      }
    };

    let myChart = new window.Chart(ctx, config);

    let selectedTimePeriod = document.getElementById('time-period');

    document.querySelector('.custom_range_btn').addEventListener('click', () => {
      setIsCalendarVisible(true);
      setIsCustomRange(true);
      setViewByDay(true);
      selectedTimePeriod.value = 'customRange';
      setIsCustom(true);
    });


    selectedTimePeriod.addEventListener('change', (e) => {
      const selectedOption = e.target.value;
      switch (selectedOption) {
        case 'thisWeek':
          setNumberOfDays(7);
          setIsCustomRange(false);
          setIsCustom(false);
          break;
        case 'lastTwoWeeks':
          setNumberOfDays(14);
          setIsCustomRange(false);
          setIsCustom(false);
          break;
        case 'thisMonth':
          setNumberOfDays(new Date(today.getFullYear(), today.getMonth() + 1, 0).getDate());
          setIsCustomRange(false);
          setIsCustom(false);
          break;
        case 'customRange':
          setIsCustomRange(true);
          setIsCustom(true);
          break;
        default:
          setNumberOfDays(7);
      }
    });

    const handlePointClick = (event) => {
      const points = myChart.getElementsAtEventForMode(event, 'nearest', { intersect: true }, false);
      if (points.length) {
        points.forEach(point => {
          const { datasetIndex, index } = point;
          console.log('Point clicked - Dataset:', datasetIndex, 'Index:', index);
        });
      }
    };


    const canvas = document.getElementById('myChart');
    canvas.addEventListener('click', handlePointClick);

    return () => {
      myChart.destroy();
      canvas.removeEventListener('click', handlePointClick);
    };

  }, [startDate, endDate, viewByDay, numberOfDays, isCustomRange]);



  const handleDateChange = (dates) => {
    const [start, end] = dates;
    setStartDate(start);
    setEndDate(end);
    setViewByDay(true);
    setIsCalendarVisible(false);
    const daysBetweenDates = (end - start) / (1000 * 60 * 60 * 24) + 1;
    if ((end - start) === 0) {
      setNumberOfDays(24);
    } else {
      setNumberOfDays(daysBetweenDates);
    }
    if (!isCalendarVisible) {
      setIsCustomRange(false);
      setIsCustom(true);
    }
  };


  const handleRadioChange = (index) => {
    setSelectedCampaign(index);
  };



  // points details
  const titleDetailContainers = document.querySelectorAll('.graph_point_detail_header');
  const detailContentContainers = document.querySelectorAll('.graph_point_detail_content');
  const arrows = document.querySelectorAll('.open_graph_point_detail');

  function setHeight(element, height) {
      element.style.height = height;
  }

  function toggleReponse(container, index) {
      // Fermer toutes les réponses ouvertes
      detailContentContainers.forEach((detailContent, idx) => {
          if (idx !== index && detailContent.classList.contains('open_reponse')) {
              setHeight(detailContent, '0px');
              detailContent.classList.remove('open_reponse');
              arrows[idx].classList.remove('rotate');
          }
      });

      // Ouvrir ou fermer la réponse sélectionnée
      if (container.classList.contains('open_reponse')) {
          setHeight(container, '0px');
          container.classList.remove('open_reponse');
          arrows[index].classList.remove('rotate');
      } else {
          const currentHeight = container.scrollHeight + 'px';
          setHeight(container, currentHeight);
          container.classList.add('open_reponse');
          arrows[index].classList.add('rotate');
      }
  }

  titleDetailContainers.forEach((questionContainer, index) => {
      questionContainer.addEventListener('click', () => {
          toggleReponse(detailContentContainers[index], index);
      });
  });



  return (
    <div>
      {/* Bannière sur toute la largeur */}
      <div className="banner">
        <div className="banner-content">
          <h1 className="banner-title">Product Database</h1>
          <p className="banner-subtitle">
            Search millions of products and see their revenue
          </p>
        </div>
        <div className="banner-buttons">
          <button className="banner-button">Take Tour</button>
          <button className="banner-button">Watch Tutorial</button>
        </div>
      </div>

      {/* main content */}
      <section className="dashboard-container">
        <div className="campaign_list_container">
          <div className="campaign_list">
            {Array.from({ length: 8 }, (_, index) => (
              <label className="campaign_list_card" htmlFor={`campaign-${index}`}>
                <input
                  type="radio"
                  name="campaign"
                  id={`campaign-${index}`}
                  checked={selectedCampaign === index}
                  onChange={() => handleRadioChange(index)}
                />
                <div className="campaign_list_card_title_container">
                  <h4>Campaign Name</h4>
                  <i className="fa fa-eye" aria-hidden="true"></i>
                </div>
                </label>
            ))}
          </div>
        </div>

        {/* Graphique */}
        <div className="graph">
          <div className="graph-title-container">
            <h3>Title</h3>
            <div className="date-select">
              <div className="custom_range">
                <button className="custom_range_btn">Custom Range</button>
                <div className="view-toggle">
                {isCustomRange && (
                  <DatePicker
                    selected={startDate}
                    onChange={handleDateChange}
                    startDate={startDate}
                    endDate={endDate}
                    selectsRange
                    inline
                  />
                )}
              </div>
              </div>
              <select name="time-period" id="time-period" onChange={(e) => setViewByDay(e.target.value === 'thisWeek')}>
                <option value="thisWeek">This Week</option>
                <option value="lastTwoWeeks">Last Two Weeks</option>
                <option value="thisMonth">This Month</option>
                {isCustom && (
                  <option value="customRange">Custom</option>
                )}
              </select>
            </div>
          </div>
          <div className="grap">
            <canvas className="income-graph" id="myChart"></canvas>
          </div>
        </div>
      </section>

      {/* Campaign point details */}
      <div className="graph_point_details_container">
        <div className="graph_point_details_list">
          <i className="fa fa-times close" aria-hidden="true"></i>

          {/* details */}
          <div className="graph_point_detail">
            <div className="graph_point_detail_header">
              <span className="graph_point_detail_title">01/03/2024</span>
              <div className="graph_point_detail_perc_bar_container">
                <p>View <span className="perc_number">50</span> %</p>
                <div className="graph_point_detail_perc_bar">
                  <div className='perc'></div>
                </div>
              </div>
              <i className="fa fa-chevron-down open_graph_point_detail" aria-hidden="true"></i>
            </div>

            <div className="graph_point_detail_content open_details">
              <p>Played at <span className='played_at'>01/03/2024 06:45</span></p>
              <div></div>
              <p>Finished at <span className='finished_at'>01/03/2024 06:55</span></p>
            </div>
          </div>




          {/* pages */}
          <ul className="graph_point_details_page_list">
            <li className="graph_point_details_page">
              <input type="radio" name="graph_point" id="graph_point_1" value="1" />
              <label htmlFor="graph_point_1">1</label>
            </li>
            <li className="graph_point_details_page">
              <input type="radio" name="graph_point" id="graph_point_2" value="2" />
              <label htmlFor="graph_point_2">2</label>
            </li>
            <li className="graph_point_details_page">
              <input type="radio" name="graph_point" id="graph_point_3" value="3" />
              <label htmlFor="graph_point_3">3</label>
            </li>
          </ul>
        </div>
      </div>





      <div className='campaign_details_conainer'>
        <div className='campaign_details'>
          <div className='header_campaign_container'>
            <div className='status_container'>
              <p>Status: <span className='status'>Rendered</span></p>
            </div>
            <div className='credit_container'>
              <p>Credit Used: <span className='credit'>17</span></p>
            </div>
            <div className='campaign_id_container'>
              <p>Campaign ID: <span className='campaign_id'>y8r6jct2</span></p>
            </div>
          </div>

          <div className='infos_campaign_container'>
            <div className='views_campaign campaign_width'>
              <div className='icon_container'>
                <p>Opened</p>
                <i className="fa fa-eye" aria-hidden="true"></i>
              </div>
              <p className='views campaign_info_value'>200</p>
            </div>
            <div className='start_campaign campaign_width'>
              <div className='icon_container'>
                <p>Started</p>
                <i className="fa fa-play" aria-hidden="true"></i>
              </div>
              <p className='start campaign_info_value'>180</p>
              <div className='more_info_container'>
                <p className='title'>Open to Start Rate</p>
                <p className='start_rate perc'>89%</p>
              </div>
            </div>
            <div className='end_campaign campaign_width'>
              <div className='icon_container'>
                <p>Ended</p>
                <i className="fa fa-flag" aria-hidden="true"></i>
              </div>
              <p className='end campaign_info_value'>160</p>
              <div className='more_info_container'>
                <p className='title'>Start to End Rate</p>
                <p className='end_rate perc'>71%</p>
              </div>
            </div>
          </div>
        </div>

        <div className='campaign_options'>
          <i className="fa fa-cog" aria-hidden="true"></i>
          <div className='setting_button'>
            <i className="fa fa-download" aria-hidden="true"></i>
            <p>download all the data</p>
          </div>
          <div className='setting_button'>
            <i className="fa fa-link" aria-hidden="true"></i>
            <p>download all the data</p>
          </div>
          <div className='setting_button'>
            <i className="fa fa-download" aria-hidden="true"></i>
            <p>download all the data</p>
          </div>

        </div>
      </div>
    </div>
  );
};

export default HomePage;
