import React from "react";
import { NavLink } from "react-router-dom";
import styled from "styled-components";
import { ReactComponent as DashboardIcon } from "../assets/logo.svg";

// Add imports for other icons you will use
// Example:
// import { ReactComponent as SettingsIcon } from "../assets/settings.svg";

const NavbarContainer = styled.div`
  background-color: #f8f9fa; // Light grey background
  min-height: 100vh;
  width: 250px;
  border-right: 1px solid #000; // A black border as per your code
`;

const Logo1 = styled(DashboardIcon)`
  max-height: 60px; // Limite la hauteur maximale du logo
  max-width: 100%; // Permet au logo de s'adapter à la largeur du conteneur
  width: auto; // S'assure que le ratio de l'image est maintenu
  margin-bottom: 1rem;
`;

const NavbarHeader = styled.div`
  padding: 2rem 1rem;
  background-color: #fff; // White background for header
  color: #333; // Dark grey text
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
`;

const UpgradeButton = styled.button`
  background-color: #007bff; // Couleur bleue
  border: none;
  padding: 10px 15px; // Ajustez le padding pour changer la taille du bouton
  color: white;
  margin-top: 1rem;
  border-radius: 5px; // Coins arrondis pour le bouton
  cursor: pointer;
  width: 100%; // Le bouton prend toute la largeur du conteneur

  &:hover {
    background-color: #0056b3; // Couleur bleue plus foncée au survol
  }
`;

const NavbarItem = styled(NavLink)`
  display: flex;
  align-items: center;
  padding: 1rem;
  color: #333;
  text-decoration: none;
  margin-left: 10px; // Indent text a bit

  &:hover {
    background-color: #f5f5f5; // Lighter grey on hover
  }

  &.active {
    background-color: #e7e7e7; // Grey background for active item
    color: #007bff; // Blue text for active item
  }
`;

const NavbarIcon = styled.span`
  margin-right: 1rem;
`;

const NavbarLabel = styled.span``;

// Add other Styled components for other icons
// Example:
// const SettingsIconStyled = styled(SettingsIcon)`
//   height: 20px;
//   width: auto;
// `;

const Navbar = ({ user }) => {
  const logout = () => {
    localStorage.removeItem("user");
    window.location.reload();
  };

  return (
    <NavbarContainer>
      <NavbarHeader>
        <Logo1 />
        <div>{user.name}</div>
        <div>7-Day Trial Left</div>
        <UpgradeButton>Upgrade</UpgradeButton>
      </NavbarHeader>
      <NavbarItem exact to="/dashboard">
        <NavbarIcon>
          <DashboardIcon />
        </NavbarIcon>
        <NavbarLabel>Dashboard</NavbarLabel>
      </NavbarItem>
      {/* Repeat for each navbar item, replacing DashboardIcon with the appropriate icon */}
      <NavbarItem to="/settings">
        <NavbarIcon>{/* Replace with <SettingsIconStyled /> */}</NavbarIcon>
        <NavbarLabel>Settings</NavbarLabel>
      </NavbarItem>
      {/* Continue adding NavbarItems for other routes */}
    </NavbarContainer>
  );
};

export default Navbar;
