import React from "react";
import { NavLink } from "react-router-dom";
import homeOff from "../assets/home_off.svg";
import homeOn from "../assets/home_on.svg";
import { ReactComponent as DashboardIcon } from "../assets/logo.svg";
import "./Navbar.css"; // Importez votre fichier CSS ici

const CustomNavLink = ({ to, end, children, iconOff, iconOn, iconSelect }) => {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <NavLink
      to={to}
      end={end}
      className={({ isActive }) =>
        isActive ? "navbar-item active" : "navbar-item"
      }
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <>
        <img
          src={isHovered ? iconSelect : isActive ? iconOn : iconOff}
          className="navbar-icon"
          alt=""
        />
        {children}
      </>
    </NavLink>
  );
};

const Navbar = ({ user }) => {
  const logout = () => {
    localStorage.removeItem("user");
    window.location.reload();
  };

  return (
    <div className="navbar-container">
      <div className="navbar-header">
        <div className="name-and-logo">
          <DashboardIcon className="logo" />
          <div>
            {user.firstName} {user.lastName}
          </div>
        </div>
        <div className="trial-text">7-Day Trial Left</div>
      </div>
      <button className="upgrade-button" onClick={logout}>
        Upgrade
      </button>
      <div className="navbar-separator"></div>
      <CustomNavLink
        to="/dashboard"
        end={true}
        iconOff={homeOff}
        iconOn={homeOn}
        iconSelect={homeSelect}
      >
        <span>Dashboard</span>
      </CustomNavLink>
      {/* Ajoutez d'autres CustomNavLink ici pour d'autres routes, avec leurs ic√¥nes respectives */}
    </div>
  );
};

export default Navbar;
