import React, { useEffect, useState } from "react";
import { NavLink, useMatch, useResolvedPath } from "react-router-dom";
import homeOff from "../assets/home_off.svg";
import homeOn from "../assets/home_on.svg";
import homeSelect from "../assets/home_select.svg";
import { ReactComponent as DashboardIcon } from "../assets/logo.svg";
import "./Navbar.css"; // Importez votre fichier CSS ici

const CustomNavLink = ({ to, children, iconOff, iconOn, iconSelect }) => {
  const [isHovered, setIsHovered] = useState(false);
  const resolved = useResolvedPath(to);
  const match = useMatch({ path: resolved.pathname, end: true });

  return (
    <NavLink
      to={to}
      className={match ? "navbar-item active" : "navbar-item"}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <>
        <img
          src={isHovered && !match ? iconSelect : match ? iconOn : iconOff}
          className="navbar-icon"
          alt=""
        />
        {children}
      </>
    </NavLink>
  );
};

const Navbar = ({ user }) => {
  const logout = () => {
    localStorage.removeItem("user");
    window.location.reload();
  };

  const [isPopupVisible, setIsPopupVisible] = useState(false);

  const togglePopup = (event) => {
    // Empêche l'événement de cliquer de se propager au-delà du conteneur `name-and-logo`
    event.stopPropagation();
    setIsPopupVisible(!isPopupVisible);
  };

  useEffect(() => {
    // Ferme la popup si on clique en dehors
    const handleClickOutside = (event) => {
      if (isPopupVisible && !event.target.closest(".navbar-header")) {
        setIsPopupVisible(false);
      }
    };

    // Ajoute un écouteur d'événements sur le document
    document.addEventListener("click", handleClickOutside);

    // Nettoyage de l'écouteur d'événements
    return () => document.removeEventListener("click", handleClickOutside);
  }, [isPopupVisible]);

  return (
    <div className="navbar-container">
      <div className="navbar-secondary">
        {/* Ajout de l'événement onClick sur le conteneur name-and-logo */}
        <div className="navbar-header" onClick={togglePopup}>
          <div className="name-and-logo">
            <DashboardIcon className="logo" />
            <div className="user-details">
              <div>
                {user.firstName} {user.lastName}
              </div>
              <div className="trial-text">7-Day Trial Left</div>
            </div>
            <span className="down-arrow">&#x2193;</span>
          </div>
          {/* Popup qui s'affiche en dessous du name-and-logo */}
          {isPopupVisible && (
            <div className="popup-container">
              <div className="popup-content">
                {/* Contenu de votre popup */}
                <div className="popup-item">Connect Shopify Store</div>
                <div className="popup-item">Connect AutoDS Account</div>
                {/* ... Autres éléments de la popup */}
              </div>
            </div>
          )}
        </div>

        <button className="upgrade-button" onClick={logout}>
          Upgrade
        </button>
        <div className="navbar-separator"></div>
        <CustomNavLink
          to="/dashboard"
          end={true}
          iconOff={homeOff}
          iconOn={homeOn}
          iconSelect={homeSelect}
        >
          <span>Dashboard</span>
        </CustomNavLink>
        <CustomNavLink
          to="/settings"
          iconOff={homeOff}
          iconOn={homeOn}
          iconSelect={homeSelect}
        >
          <span>Settings</span>
        </CustomNavLink>
        {/* Ajoutez d'autres CustomNavLink ici pour d'autres routes, avec leurs icônes respectives */}
      </div>
    </div>
  );
};

export default Navbar;
