import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import useFetch from "../hooks/useFetch";

const LoginContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100vh;
  flex-direction: column;
`;

const GoogleLoginButtonContainer = styled.div`
  margin-top: 1rem;
`;

const Login = () => {
  const { handleGoogle, loading, error } = useFetch(
    "http://localhost:5152/login"
  );
  const [googleSDKLoaded, setGoogleSDKLoaded] = useState(false);

  useEffect(() => {
    // Charge le SDK de Google dynamiquement
    const script = document.createElement("script");
    script.src = "https://accounts.google.com/gsi/client";
    script.onload = () => {
      setGoogleSDKLoaded(true);
    };
    document.body.appendChild(script);

    return () => {
      // Nettoyage
      document.body.removeChild(script);
    };
  }, []);

  useEffect(() => {
    if (googleSDKLoaded) {
      // Initialise le bouton de connexion Google après le chargement du SDK
      window.google.accounts.id.initialize({
        client_id:
          "509938645259-pmtvqe7ni98ivq69k4omgimi5mbfl4uv.apps.googleusercontent.com",
        callback: handleGoogle,
      });

      window.google.accounts.id.renderButton(
        document.getElementById("googleLoginButton"),
        {
          theme: "outline",
          size: "large",
          shape: "pill",
        }
      );
    }
  }, [googleSDKLoaded, handleGoogle]);

  return (
    <>
      <nav style={{ padding: "2rem" }}>
        <Link to="/">Go Back</Link>
      </nav>
      <LoginContainer>
        <header style={{ textAlign: "center" }}>
          <h1>Login to continue</h1>
        </header>
        <main>
          {error && <p style={{ color: "red" }}>{error}</p>}
          {loading ? (
            <div>Loading....</div>
          ) : (
            <GoogleLoginButtonContainer id="googleLoginButton"></GoogleLoginButtonContainer>
          )}
        </main>
      </LoginContainer>
      <footer></footer>
    </>
  );
};

export default Login;
