import React from "react";
import { NavLink } from "react-router-dom";
import { ReactComponent as DashboardIcon } from "../assets/logo.svg";
import "./Navbar.css"; // Importez votre fichier CSS ici

const Navbar = ({ user }) => {
  const logout = () => {
    localStorage.removeItem("user");
    window.location.reload();
  };

  return (
    <div className="navbar-container">
      <NavbarHeader>
        <div className="name-and-logo">
          <Logo1 className="logo" />
          <div>{user.name}</div>
        </div>
        <div className="trial-text">7-Day Trial Left</div>
        <UpgradeButton className="upgrade-button">Upgrade</UpgradeButton>
      </NavbarHeader>
      <NavLink exact to="/dashboard" className="navbar-item">
        <DashboardIcon className="navbar-icon" />
        <span>Dashboard</span>
      </NavLink>
      {/* Répétez pour chaque élément de la navbar, en remplaçant DashboardIcon par l'icône appropriée */}
      <NavLink to="/settings" className="navbar-item">
        <span className="navbar-icon">{/* Icône des paramètres */}</span>
        <span>Settings</span>
      </NavLink>
      {/* Continuez à ajouter des NavLink pour les autres routes */}
    </div>
  );
};

export default Navbar;
