import React from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import { ReactComponent as DashboardIcon } from "../assets/logo.svg"; // Assure-toi que le chemin est correct

const NavbarContainer = styled.div`
  background-color: #fff;
  min-height: 100vh;
  width: 250px; // ou la largeur que tu veux
  border-right: 1px solid #9499a1; // Remplace l'ombre par un trait noir
`;

const NavbarHeader = styled.div`
  padding: 1rem;
  background-color: #blue; // Utilise ta propre couleur
  color: white;
  display: flex; // Ajouté pour centrer le logo et le texte verticalement
  flex-direction: column; // Ajouté pour organiser le contenu en colonne
  align-items: center; // Centré horizontalement
  text-align: center; // Assure que le texte est centré
`;

const Logo = styled(DashboardIcon)`
  height: 50px; // Ajuste la taille du logo selon tes besoins
  width: auto; // Garde l'aspect ratio du logo
  margin-bottom: 1rem; // Espace entre le logo et le texte en dessous
`;

const NavbarItem = styled(Link)`
  display: flex;
  align-items: center;
  padding: 1rem;
  color: #333;
  text-decoration: none;
  &:hover {
    background-color: #f5f5f5;
  }
  &.active {
    background-color: #e7e7e7;
  }
`;

const NavbarIcon = styled.span`
  margin-right: 1rem;
`;

const NavbarLabel = styled.span``;

const Navbar = ({ user }) => {
  const logout = () => {
    localStorage.removeItem("user");
    window.location.reload();
  };
  return (
    <NavbarContainer>
      <NavbarHeader>
        <Logo />
        {user.name}
        <div>7-Day Trial Left</div>
      </NavbarHeader>
      <NavbarItem to="/dashboard">
        <NavbarIcon></NavbarIcon>
        <NavbarLabel>Dashboard</NavbarLabel>
      </NavbarItem>
      {/* Répéter pour chaque élément de menu */}
    </NavbarContainer>
  );
};

export default Navbar;
