import React from "react";
import { NavLink } from "react-router-dom";
import styled from "styled-components";
import { ReactComponent as DashboardIcon } from "../assets/logo.svg";

const NavbarContainer = styled.div`
  background-color: #fff;
  min-height: 100vh;
  width: 250px;
  border-right: 1px solid #000; // Trait noir au lieu de l'ombre
`;

const NavbarHeader = styled.div`
  padding: 1rem;
  background-color: #0000ff; // Couleur bleue
  color: white;
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
`;

const Logo = styled(DashboardIcon)`
  height: 50px;
  width: auto;
  margin-bottom: 1rem;
`;

const NavbarItem = styled(NavLink)`
  display: flex;
  align-items: center;
  padding: 1rem;
  color: #333;
  text-decoration: none;

  &:hover {
    background-color: #f5f5f5;
  }

  &.active {
    background-color: #e7e7e7;
    color: #0000ff;
  }
`;

const NavbarIcon = styled.span`
  margin-right: 1rem;
`;

const NavbarLabel = styled.span``;

const Navbar = ({ user }) => {
  const logout = () => {
    localStorage.removeItem("user");
    window.location.reload();
  };

  return (
    <NavbarContainer>
      <NavbarHeader>
        <Logo />
        {user.name}
        <div>7-Day Trial Left</div>
        <button onClick={logout}>Logout</button>
      </NavbarHeader>
      <NavbarItem exact to="/dashboard">
        <NavbarIcon></NavbarIcon>
        <NavbarLabel>Dashboard</NavbarLabel>
      </NavbarItem>
      {/* Répéter pour chaque élément de menu, par exemple : */}
      <NavbarItem to="/settings">
        <NavbarIcon>{/* Icon for settings */}</NavbarIcon>
        <NavbarLabel>Settings</NavbarLabel>
      </NavbarItem>
      {/* Ajouter d'autres NavbarItem ici pour d'autres routes */}
    </NavbarContainer>
  );
};

export default Navbar;
