import React from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import { ReactComponent as DashboardIcon } from "../assets/logo.svg"; // Importe tes icônes SVG comme ça

const NavbarContainer = styled.div`
  background-color: #fff;
  min-height: 100vh;
  width: 250px; // ou la largeur que tu veux
  box-shadow: 2px 0 5px rgba(0, 0, 0, 0.1);
`;

const NavbarHeader = styled.div`
  padding: 1rem;
  background-color: #blue; // Utilise ta propre couleur
  color: white;
`;

const NavbarItem = styled(Link)`
  display: flex;
  align-items: center;
  padding: 1rem;
  color: #333;
  text-decoration: none;
  &:hover {
    background-color: #f5f5f5;
  }

  &.active {
    background-color: #e7e7e7;
  }
`;

const NavbarIcon = styled.span`
  margin-right: 1rem;
`;

const NavbarLabel = styled.span``;

const Navbar = () => {
  return (
    <NavbarContainer>
      <NavbarHeader>
        Samuel Vasseyr
        <div>7-Day Trial Left</div>
      </NavbarHeader>
      <NavbarItem to="/dashboard">
        <NavbarIcon>
          <DashboardIcon />
        </NavbarIcon>
        <NavbarLabel>Dashboard</NavbarLabel>
      </NavbarItem>
      {/* Répéter pour chaque élément de menu */}
    </NavbarContainer>
  );
};

export default Navbar;
